<?php namespace App\Models\Contacts;		 require_once __DIR__ . '../../../vendor/autoload.php';use DataBase\Connect\DataBaseConnect;use App\Validation\Validation;trait Contacts{    private $result;    private $arr;    private $data;    public function getData($query)    {        $this->connect();        $dbConnection = $this->getConnection();        $this->query = $query;        $this->result = $dbConnection->query($this->query);            $this->closeConnection();    }    public function getResult(){        while($this->arr = mysqli_fetch_assoc($this->result)){            $this->data[] = $this->arr;        }        return ($this->data);    }    public function insertData($data){        $this->connect();        $dbConnection = $this->getConnection();                $username = $data['user_name'];                $userphone = '+7'.$data['user_phone'];                $query = "INSERT INTO contact_list(`user_name`,`user_phone`,`datetime`,`isActive`) VALUES('$username','$userphone',NOW(),1)";                if($dbConnection->query($query)){                return true;                }                else{                    exit($dbConnection->mysqli_error());                }        $this->closeConnection();    }    public function deleteData($data){        $this->connect();        $dbConnection = $this->getConnection();        $query = "UPDATE `contact_list` SET isActive = 0 WHERE id='$data'";        if($dbConnection->query($query)){            return true;        }        else{            exit($dbConnection->mysqli_error());        }    }}?>